import { StatusBar } from "expo-status-bar";
import React, { useState } from "react";
import { LinearGradient } from "expo-linear-gradient";
import { easeGradient } from 'react-native-easing-gradient'
import {col} from "./clr.js"

import {
  StyleSheet,
  Text,
  View,
  Image,
  TextInput,
  Button,
  TouchableOpacity,
  ActivityIndicator,
  Pressable
} from "react-native";
import SomeComponent from "../CustomBackground.js";
import methods, { registration, signIn } from '../../Firebase/methods'

 
const ProviderRegistrationScreen = ({navigation}) => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [name,setName] = useState("");
  const [surname,setsurname] = useState("");
  const [loadingStatus,setLoading] = useState(false);
  const [time,setTime] = useState(0)
 
  const onRegistrationClick = async () => {
    try {
      if (email !== '' && password !== '' && name !== '' && surname !== '') {
        console.log(email)
        await registration(email,password,name,surname)
        navigation.navigate('Main')
      }
    } catch (error) {
      console.log(error.message)
    }
  }

  const onLoginClick = async () =>{
      navigation.navigate('ConsumerLogin')
  }

  return (
    <View style={styles.container}>
      { loadingStatus ? 
        <View style={{flex:1,justifyContent:"center",alignContent:"center"}}>
          <ActivityIndicator size={70} color={col.secondaryColor} />
        </View> :
        <SomeComponent>
        <View style={styles.container}>
            <View style={styles.titleContainer}>
                <Text style={styles.titleText}>Provider Registration</Text>
            </View>
            <View style={styles.formContainer}>
            <View style={styles.inputView}>
                    <TextInput
                    style={styles.TextInput}
                    placeholder="Name"
                    placeholderTextColor="#003f5c"
                    onChangeText={(name) => setName(name.replace(' ',''))}
                    />
                </View>
                <View style={styles.inputView}>
                    <TextInput
                    style={styles.TextInput}
                    placeholder="surname"
                    placeholderTextColor="#003f5c"
                    onChangeText={(surname) => setsurname(surname.replace(' ',''))}
                    />
                </View>
                <View style={styles.inputView}>
                    <TextInput
                    style={styles.TextInput}
                    placeholder="Email"
                    placeholderTextColor="#003f5c"
                    onChangeText={(email) => setEmail(email.replace(' ',''))}
                    />
                </View>
        
                <View style={styles.inputView}>
                    <TextInput
                    style={styles.TextInput}
                    placeholder="Geslo"
                    placeholderTextColor="#003f5c"
                    secureTextEntry={true}
                    onChangeText={(password) => setPassword(password)}
                    />
                </View>
        
                <Pressable onPress={onLoginClick}>
                    <Text style={styles.forgot_button}>Already registrated? Login!</Text>
                </Pressable>
        
                <TouchableOpacity style={styles.loginBtn} onPress={onRegistrationClick}>
                    <Text style={styles.loginText}>LOGIN</Text>
                </TouchableOpacity>
            </View>
        </View>
        </SomeComponent>}
      </View>
  );
}
 
const styles = StyleSheet.create({
  container: {
    flex: 1
  },
 formContainer:{
    flex: 4,
    backgroundColor: "transparent",
    alignItems: "center",
    justifyContent: "center",
 },
  inputView: {
    backgroundColor: col.textColorSecondary,
    borderRadius: 30,
    width: 200,
    height: 45,
    marginBottom: 20,
    alignItems: "center",
  },
 
  TextInput: {
    color: col.clrText,
    fontFamily:"Light",
    textAlign:"center",
    height: 50,
    flex: 1,
  },
 
  forgot_button: {
    color: col.clrText,
    fontFamily:"Regular",
    height: 30,
    marginBottom: 30,
  },
 
  loginBtn: {
    width: 300,
    borderRadius: 25,
    height: 50,
    alignItems: "center",
    justifyContent: "center",
    marginTop: 40,
    backgroundColor: col.secondaryColor,
  },
  loginText: {
    color: col.clrText,
    fontFamily:"Bold",
  },
  titleContainer:{
    flex: 1,
    backgroundColor: "transparent",
    alignItems: "center",
    justifyContent: "flex-end",
  },
  titleText:{
    color: col.clrText,
    fontFamily:"Bold",
    fontSize:40,
  },
});

export default ProviderRegistrationScreen;
