import React, { useContext, useEffect, useState } from 'react';
import { View, Text, StyleSheet, Image, Button, Pressable } from 'react-native';

import * as ImagePicker from 'expo-image-picker';
import TesseractOcr, { LANG_ENGLISH } from 'react-native-tesseract-ocr';
import SomeComponent from './CustomBackground';
import ImgToBase64 from 'react-native-image-base64';
import { FlatList, ScrollView } from 'react-native-gesture-handler';
import { col } from './clr';
import Accordion from 'react-native-collapsible/Accordion';
import { TextInput } from 'react-native-paper';
import { Picker } from '@react-native-picker/picker';
import { useDispatch } from 'react-redux';
import DateTimePicker from '@react-native-community/datetimepicker';


function RecepieScanScreen() {
  // The path of the picked image
  const [pickedImagePath, setPickedImagePath] = useState('');
  const[imageText,setText] = useState('');
  const[showWindow,setWindow] = useState(false);
  const[itemList,setItemList] = useState([]);
  const[check,setCheck] = useState(true)
  
  
  String.prototype.replaceAll = function(str1, str2, ignore) 
  {
      return this.replace(new RegExp(str1.replace(/([\/\,\!\\\^\$\{\}\[\]\(\)\.\*\+\?\|\<\>\-\&])/g,"\\$&"),(ignore?"gi":"g")),(typeof(str2)=="string")?str2.replace(/\$/g,"$$$$"):str2);
  }

  const sendRequest = async (base64) =>{
    console.log(base64.length)
    let result = await fetch('https://strassium.loca.lt/uploader', {
  method: 'POST',
  headers: {
    Accept: 'application/json',
    'Content-Type': 'application/json'
  },
  body: JSON.stringify({
    imageString:base64
  })
}).then(response => response.json()).then((responseJson) => {setText(responseJson.text.replaceAll(' =+-n ', '\n'));setItemList(responseJson.text.split(' =+-n '));itemList.pop();setWindow(true)})
  
  }

  // This function is triggered when the "Select an image" button pressed
  const showImagePicker = async () => {
    // Ask the user for the permission to access the media library 
    const permissionResult = await ImagePicker.requestMediaLibraryPermissionsAsync();

    if (permissionResult.granted === false) {
      alert("You've refused to allow this appp to access your photos!");
      return;
    }

    const result = await ImagePicker.launchImageLibraryAsync({
      
      base64: true,
      quality:0.5,
      
      allowsEditing:true
    });

    

    if (!result.cancelled) {
