import { StatusBar } from 'expo-status-bar';
import React from 'react';
import { Alert, Platform, Pressable, StyleSheet, Text, TouchableHighlight, FlatList, View } from 'react-native';
import FlatListBasics from './Components/FlatList';

const STATUS_BAR_HEIGHT = Platform.select({ios:20, android:24});

const onClick = () => {
  alert("lol");
}

const FridgeScreen = ({navigation}) => {

    return (
        <View style={styles.container}>
            <View style={{ height: STATUS_BAR_HEIGHT}} />
        <View style={styles.topBarContainer}>
          <Text style={styles.titleStyle}>Hladilnik</Text>
        </View>
        <View style={styles.mainContainer}>
          <View style={styles.legendcontainer}>
              <View style={{flex:1,justifyContent:"center"}}>
              <Text style={styles.nameStyle}>Ime</Text>
            </View>
            <View style={{flex:1,justifyContent:"center",alignItems:"flex-end"}}>
              <Text style={styles.expStyle}>Datum uporabe</Text>
            </View>
            </View>
            
          <View style={styles.listContainer}>
          <FlatList
            data={[
              {key: 'Eggs', expDate: "10"},
              {key: 'Dan', expDate: "10"},
              {key: 'lalala', expDate: "10"},
              {key: 'Jackson', expDate: "10"},
              {key: 'James', expDate: "10"},
              {key: 'Joel', expDate: "10"},
              {key: 'John', expDate: "10"},
              {key: 'Jillian', expDate: "10"},
              {key: 'Jimmy', expDate: "10"},
            ]}
            renderItem={({item}) =><Pressable style={{flexDirection:"row"}} onPress={onClick}><View style={styles.itemContainer}>
              <Text style={styles.item}>{item.key}</Text>
            </View>
            <View style={[styles.itemContainer,{alignItems:"flex-end"}]}>
              <Text style={styles.item}>{item.expDate} dni</Text>
            </View>
            </Pressable>}
          />
      </View>
        </View>
        </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: 'transparent',
  },
  topBarContainer:{
      flex:1,
      backgroundColor:"transparent",
      justifyContent:"center",
      alignItems:"center",
      borderBottomWidth:1
 },
 pressableStyle:{
     flex:1,
    alignItems:"center",
    justifyContent:"center",
    borderColor:"gray",
    borderWidth:1,
 },

 mainContainer:{
    flex:8,
  },
  titleStyle: {
    fontSize: 22,
    textAlign:'center',
  },listContainer: {
    flex: 8,
    alignContent:"flex-start",
    
   },
   itemContainer:{
     flex:1
   },
   item: {
     padding: 10,
     fontSize: 16,
     height: 44,
   },
   legendcontainer:{flex:1,flexDirection:"row",justifyContent:"center", borderBottomWidth:1},
   nameStyle:{
     fontSize:20,paddingLeft:10
   },
   expStyle:{
    fontSize:20,paddingRight:10
  }
});
export default FridgeScreen
