import { StatusBar } from 'expo-status-bar';
import React, { useState } from 'react';
import { Image, Platform, Pressable, StyleSheet, Text, TouchableHighlight, FlatList, View, Dimensions } from 'react-native';
import Swipeable from 'react-native-swipeable';
import { col } from './clr';

const STATUS_BAR_HEIGHT = Platform.select({ios:20, android:24});


const ShoppingListScreen = ({navigation,route}) => {

  
  const [state,setState] = useState({
    data:[
      {key: 'Eggs', expDate: "14",quantity:2},
      {key: 'Dan', expDate: "3",quantity:2},
      {key: 'lalala', expDate: "8",quantity:2},
      {key: 'Jackson', expDate: "9",quantity:2},
      {key: 'James', expDate: "14",quantity:2},
      {key: 'This is a very special beef', expDate: "14",quantity:2},
      {key: 'Marjan', expDate: "14",quantity:2},
      {key: 'LoL', expDate: "14",quantity:2},
      {key: 'HEHEHEEH', expDate: "14",quantity:2},
    ]
});



  const listElement = ({item}) =>{

    const onClick = () => {
      let dataTemp = state.data;
      dataTemp[dataTemp.indexOf(item)].quantity--;
      if(dataTemp[dataTemp.indexOf(item)].quantity > 0){
        setState({data:dataTemp});
      }else{
        onSwipe();
      }
    }

    const onSwipe = () => {
      let dataTemp = state.data;
      dataTemp.splice(dataTemp.indexOf(item),1);
      console.log(dataTemp);
      setState({data:dataTemp});
    }

    const rightContent = <View style={{justifyContent:'center',alignItems:'flex-start',flex:1}}>
        <Text style={{fontSize:16}}>DELETE</Text>
          </View>
       ;
      ;

    return(
      <Swipeable rightContent={rightContent} rightActionActivationDistance={Dimensions.get("window").width / 2} onRightActionRelease={onSwipe} >
        <View style={styles.mainCardView}>
          <View style={{flexDirection: 'row', alignItems: 'center'}}>
            <View style={styles.subCardView}>
              <Image
                source={require('./Icons/hrana.png')}
                resizeMode="contain"
                style={{
                  borderRadius: 25,
                  height: 50,
                  width: 50,
                }}
              />
            </View>
            <View style={{marginLeft: 12, justifyContent:'center', flex:1}}>
                <Text
                style={{
                  fontSize: 14,
                  color: 'black',
                  fontWeight: 'bold',
                  textTransform: 'capitalize',
                }}>
                {item.key}
              </Text>
                <Text
                  style={{
                    color: 'gray',
                    fontSize: 12,
                    marginTop:3,
                    marginLeft:2
                  }}>
                  {item.expDate} days left
                </Text>
            </View>
          </View>
          <Pressable
            style={{
              height: 25,
              backgroundColor: col.mainColor,
              borderWidth: 0,
              width: 25,
              marginLeft: -26,
              alignItems: 'center',
              justifyContent: 'center',
              borderRadius: 50,
            }} onPress={onClick}>
            <Text style={{color: 'white'}}>
              {item.quantity}
            </Text>
          </Pressable>
        </View>
      </Swipeable>
    );
  }

  
    return (
        <View style={styles.mainContainer}>            
          <View style={styles.listContainer}>{typeof state.data !== 'undefined' ?
              state.data.length != 0 ? <FlatList
          data={state.data}
          extraData={state}
            renderItem={({item}) => listElement({item})}
          />
          : <Text style={styles.notifStyle}>Uh, oh Vaš seznam je prazen :(</Text> : <Text style={styles.notifStyle}>Uh, oh Vaš seznam je prazen :(</Text> }
      </View>
        </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: 'transparent',
  },
  topBarContainer:{
      flex:1,
      backgroundColor:"transparent",
      justifyContent:"center",
      alignItems:"center",
      borderBottomWidth:1
 },
 pressableStyle:{
     flex:1,
    alignItems:"center",
    justifyContent:"center",
    borderColor:"gray",
    borderWidth:1,
 },

 mainContainer:{
    flex:8,
  },
  titleStyle: {
    fontSize: 22,
    textAlign:'center',
    bottom:15
  },listContainer: {
    flex: 8,
    alignContent:"flex-start",
   },
   itemContainer:{
     flex:1,
     backgroundColor:'black',

   },
   item: {
    fontFamily: "Bold",
    color: col.clrBlack,
     marginTop: 10,
     paddingHorizontal:10,
     fontSize: 16,
     paddingBottom:2,borderWidth:1,borderRadius:15,paddingHorizontal:6,marginLeft:6
   },
   legendcontainer:{backgroundColor:col.mainColor,flex:1,flexDirection:"row",justifyContent:"center", borderBottomWidth:1},
   nameStyle:{
    fontFamily: "Bold",
    color: col.clrBlack,
     fontSize:20,borderWidth:1,borderRadius:15,paddingHorizontal:8,alignSelf:"flex-start",marginLeft:6
   },nameSubStyle:{
    fontFamily: "Medium",
    color: col.clrText,
    fontSize:16,paddingLeft:10,borderRadius:15,paddingHorizontal:6, marginTop:5
  },
   expStyle:{
     fontFamily: "Bold",
    color: col.clrBlack,
    fontSize:20,
    borderWidth:1,
    borderRadius:15,
    paddingHorizontal:10
  },
  notifStyle:{
    fontSize:16,
    top:30,
    alignSelf:'center'
  },
  itemSub:{
    fontFamily:"Regular",
    paddingHorizontal: 10,
     fontSize: 14,marginTop:5
  },itemQua:{
    fontFamily: "Regular",
    marginTop: 10,
    paddingHorizontal:10,
    fontSize: 16,
    paddingBottom:2,borderWidth:1,borderRadius:15,paddingHorizontal:6,
  },
  mainCardView: {
    height: 90,
    alignItems: 'center',
    justifyContent: 'center',
    backgroundColor: 'white',
    borderRadius: 15,
    shadowColor: 'gray',
    shadowOffset: {width: 0, height: 0},
    shadowOpacity: 1,
    shadowRadius: 8,
    elevation: 8,
    flexDirection: 'row',
    justifyContent: 'space-between',
    paddingLeft: 16,
    paddingRight: 14,
    marginTop: 6,
    marginBottom: 6,
    marginLeft: 16,
    marginRight: 16,
  },
  subCardView: {
    height: 50,
    width: 50,
    borderRadius: 25,
    backgroundColor: 'white',
    borderColor: 'white',
    borderWidth: 1,
    borderStyle: 'solid',
    alignItems: 'center',
    justifyContent: 'center',
  },
});
export default ShoppingListScreen
