import { StatusBar } from 'expo-status-bar';
import React,{useRef} from 'react';
import { Platform, Pressable, StyleSheet, Text, Animated, FlatList, View, Dimensions } from 'react-native';
import Swipeable from 'react-native-swipeable';
import { col } from './clr';
import { connect } from 'react-redux';
import { bindActionCreators } from 'redux';
import {removeStorageItem} from './reduxComponents/removeStorageItemAction'
import {removeStorageSingleItem} from './reduxComponents/removeStorageItemSingleAction'
import {removeStorageItems} from './reduxComponents/removeStorageItems'
import SomeComponent from './CustomBackground';
import { TextInput } from 'react-native-gesture-handler';
import { addStorageItem } from './reduxComponents/addStorageItemAction';
import { Picker } from '@react-native-picker/picker';


const STATUS_BAR_HEIGHT = Platform.select({ios:20, android:24});

class StorageListScreen extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      y: 0,
      selectedValue:'None',
      check: 'none',
      appear:true,
      appearAdd:false,
      key:'none',
      expDate:'none',
      quantity:'none',
      measure:'none',
      avgPrice:1
    };
  }

  

  render() {
  const handleSignOut = async () => {
    try {
      await auth.signOut();
    } catch (error) {
      console.log(error);
    }
  };

    const addAllPress = () => {
      this.props.removeStorageItems('')
    }
  const listElement = ({item}) =>{

    const onClick = () => {
      this.props.removeStorageSingleItem(item)
    }

    const onSwipe = () => {
      this.props.removeStorageItem(item)
      console.log(item)
    }

    const rightContent = <View style={{justifyContent:'center',alignItems:'flex-start',flex:1}}>
        <Text style={{fontSize:16,fontFamily: 'dyslectic'}}>DELETE</Text>
          </View>
       ;
      ;

    return(
      <Swipeable rightContent={rightContent} rightActionActivationDistance={Dimensions.get("window").width / 4} onRightActionRelease={onSwipe} >
        <View style={styles.mainCardView}>
        <View style={{flexDirection: 'row', alignItems: 'center'}}>

          <View style={{marginLeft: 12, justifyContent:'center', flex:1}}>
            <View style={{flexDirection:'row',alignItems:'center'}}>
            <Text
              style={{
                fontSize: 20,
                color: 'white',
                fontWeight: 'bold',
                textTransform: 'capitalize',
                fontFamily: 'dyslectic'
              }}>
              {item.key} 
            </Text>
            <Text style={{
                  color: 'white',
                  fontSize: 16,
                }}>  [ {item.measure} ]</Text>
            </View>
              <Text style={{
                  color: col.secondaryColor,
                  fontSize: 15,
                  marginTop:2,
                  fontFamily: 'dyslectic'
                }}>{item.expDate} days left</Text>
          </View>
          <View style={{flex:1, flexDirection:'row'}}>
            <View style={{flex:1, justifyContent:'center',alignItems:'flex-end'}}>
              
            </View>
          <Pressable
            style={{
              flex:1,
              marginLeft: -30,
              alignItems: 'flex-end',
              justifyContent: 'center',
            }} onPress={onClick}>
            <Text style={{color: col.mainColor,fontSize:18,fontWeight:'bold',textAlign:'center'}}>
              {item.quantity}
            </Text>
          </Pressable>
          </View>
          </View>
        </View>
      </Swipeable>
    );
  }
  const emptyListRender = () =>{
    return(
      <View style={{flex:1,justifyContent:'center',alignItems:'center',alignContent:'center'}}>
          <Text style={{fontSize:20,alignSelf:'center',color:col.textColorPrimary}}>YOUR LIST IS EMPTY</Text>
      </View>
    );
  }
  const addItem = () =>{
    if(this.state.key != 'none' && this.state.quantity != 'none' && this.state.measure != 'none' && this.state.expDate != 'none' ){
      const item_ = {key:this.state.key,expDate:this.state.expDate,quantity:this.state.quantity,avgPrice:2.5,measure:this.state.measure}
      console.log(item_)
      this.props.addStorageItem(item_);
      this.setState({appearAdd:false});
      this.setState({key:'none',quantity:'none',measure:'none',expDate:'none'})
    }else{
      alert('All the fields are mandatory!')
    }
  }
    return (
        <View style={styles.mainContainer}>            
          <View style={styles.listContainer}>
          { this.state.appearAdd == true ? 
              <View style={{flex:14,backgroundColor:'transparent',justifyContent:'space-around',alignItems:'center',alignContent:'center'}}>
                <Pressable style={{flex:0.5,justifyContent:'flex-end'}} onPress={() => this.setState({appearAdd:false})}>
                    <Text>BACK</Text>
                  </Pressable>
                <View style={{flex:9,width:'100%',justifyContent:'space-around',alignItems:'center',alignContent:'center'}}>
                  <Text style={{fontSize:24,color:col.textColorPrimary}}>ADD ITEM</Text>
                <TextInput
                    style={{height:55,width:'85%',textAlign:'center',textAlignVertical:'center',borderRadius:25,borderWidth:0.5,color:col.mainColor,backgroundColor:col.textColorPrimary}}
                    placeholder="NAME"
                    placeholderTextColor={col.mainColor}
                    onChangeText={(_key) => this.setState({key:_key}) }

                    />
                    <TextInput
                    style={{height:55,width:'85%',textAlign:'center',textAlignVertical:'center',borderRadius:25,borderWidth:0.5,color:col.mainColor,backgroundColor:col.textColorPrimary}}
                    placeholder="QUANTITY"
                    placeholderTextColor={col.mainColor}
                    onChangeText={(_quantity) => this.setState({quantity:_quantity})}
                    />
                    <View style={{ height:55,width:'85%',textAlign:'center',textAlignVertical:'center',borderRadius:25,borderWidth:0.5,backgroundColor:col.textColorPrimary }}>
                    <Picker
                selectedValue={this.state.selectedValue}
                style={{ height:55,width:'100%',textAlign:'center',textAlignVertical:'center',borderRadius:25,borderWidth:0.5,color:col.subTextColor }}//a
                dropdownIconColor={col.subTextColor}
                dropdownIconRippleColor={col.subTextColor}
                onValueChange={(itemValue, itemIndex) =>
                    this.setState({measure:itemValue, selectedValue:itemValue})
                } 
                
                >
                <Picker.Item label="Kilograms [Kg]" value="Kg" />
                <Picker.Item label="Grams [g]" value="g" />
                <Picker.Item label="Liters [L]" value="L" />
                <Picker.Item label="Milliliters [mL]" value="mL" />
                </Picker>
                    </View>
                    <TextInput
                    style={{height:55,width:'85%',textAlign:'center',textAlignVertical:'center',borderRadius:25,borderWidth:0.5,color:col.mainColor,backgroundColor:col.textColorPrimary}}
                    placeholder="EXP. DATE"
                    placeholderTextColor={col.mainColor}
                    onChangeText={(_expDate) => this.setState({expDate:_expDate})}
                    
                    />
                    
                    </View>
                    <View style={{flex:1,justifyContent:'center',alignItems:'center'}}>
                    <Pressable style={{width:50,height:50,borderColor:col.textColorPrimary,justifyContent:'center',alignItems:'center'}} onPress={addItem}>
                        <Text style={{fontSize:18,color:col.textColorPrimary}}>ADD</Text>
                    </Pressable>
                    </View>
                    
              </View> :
          <View style={{flex:16.5}}>
            <View style={{flex:1.5}}>
              <View style={{flex:1,backgroundColor:col.textColorPrimary,borderBottomColor:col.secondaryColor,borderTopWidth:0.5,elevation:4,borderRadius:15,marginLeft:5,marginRight:5,marginTop:5}}>
            { typeof this.props.dataReducer.storageList !== 'undefined' ?
              this.props.dataReducer.storageList != 0 ? 
                  <View style={{flex:1.2,alignItems:'center',flexDirection:'row'}}>
                      <Pressable style={{flex:1,justifyContent:'flex-start',marginLeft:10}} onPress={addAllPress}>
                          <Text style={{fontSize:18,color:col.mainColor,fontFamily: 'dyslectic'}}>MOVE ALL</Text>
                      </Pressable>
                      <Pressable style={{flex:1,alignItems:'flex-end',marginRight:10}} >
                          <Text style={{fontSize:18,color:col.secondaryColor,fontFamily: 'dyslectic'}} onPress={() => this.setState({appearAdd:true})}>ADD ITEM</Text>
                      </Pressable>
                  </View> : 
                  <View style={{flex:1,alignItems:'center',flexDirection:'row'}} >
                  <Pressable style={{flex:1,alignItems:'center'}} onPress={() => this.setState({appearAdd:true})}>
                      <Text style={{fontSize:18,color:col.secondaryColor,fontFamily: 'dyslectic'}}>ADD ITEM</Text>
                  </Pressable>
              </View> : 
              <View style={{flex:1,alignItems:'center',flexDirection:'row'}} >
                      <Pressable style={{flex:1,alignItems:'center'}} onPress={() => this.setState({appearAdd:true})}>
                          <Text style={{fontSize:13,color:col.secondaryColor,fontFamily: 'dyslectic'}}>ADD ITEM</Text>
                      </Pressable>
                  </View>
                }
                </View>
              </View>
              <View style={{flex:15}}>
                {typeof this.props.dataReducer.storageList !== 'undefined' ?
              this.props.dataReducer.storageList != 0 ?
                <View style={{flex:14}}>
                  <FlatList
                    data={this.props.dataReducer.storageList}
                    extraData={this.props.dataReducer}
                    renderItem={({item}) => listElement({item})}
                  />
                </View>:emptyListRender():emptyListRender() }
              </View>
            </View>}
      </View>
        </View>
  );
          }
  
}

const styles = StyleSheet.create({
 mainContainer:{
    flex:8,
  },
  listContainer: {
    flex: 8,
    justifyContent:'center',
    backgroundColor:col.secondaryColor
   },
  mainCardView: {
    height: 90,
    alignItems: 'center',
    justifyContent: 'center',
    backgroundColor: col.textColorPrimary,
    borderRadius: 15,
    shadowColor: 'gray',
    shadowOffset: {width: 0, height: 0},
    shadowOpacity: 1,
    shadowRadius: 8,
    elevation: 8,
    flexDirection: 'row',
    justifyContent: 'space-between',
    paddingLeft: 16,
    paddingRight: 14,
    marginTop: 6,
    marginBottom: 6,
    marginLeft: 20,
    marginRight: 20,
  },
  subCardView: {
    height: 50,
    width: 50,
    borderRadius: 25,
    backgroundColor: 'transparent',
    borderStyle: 'solid',
    alignItems: 'center',
    justifyContent: 'center',
  },
});

const mapStateToProps = (state) => {
  const { dataReducer } = state
  return { dataReducer }
};
const mapDispatchToProps = dispatch => (
  bindActionCreators({
    removeStorageItem: removeStorageItem,
    removeStorageSingleItem: removeStorageSingleItem,
    removeStorageItems: removeStorageItems,
    addStorageItem: addStorageItem
  }, dispatch)
);

export default connect(mapStateToProps, mapDispatchToProps)(StorageListScreen);
