import * as React from 'react';
import { View, Image, Text, StyleSheet, Pressable } from 'react-native';
import { LinearGradient } from "expo-linear-gradient";
import { easeGradient } from 'react-native-easing-gradient'
import { col } from './clr';
import MainBackground from './Components/MainBackground'



const EntranceScreen = ({navigation}) => {
    const onConsumerClick = () => {
        navigation.navigate("ConsumerLogin");
    };
    const onCompanyClick = () => {
      navigation.navigate("CompanyLogin");
  };
  const { colors, locations } = easeGradient({
    colorStops: {
      0: {
        color: col.secondaryColor,
      },
      1: {
        color: col.mainColor,
      },
    },
  })
    return(
      <View style={{flex:1}}>
        <MainBackground style={styles.container}>
            <View style={styles.textContainer} >
                <View style={{flex:1, alignItems:'flex-end',flexDirection:'row'}}>
                <Text style={styles.textStyle}>SMART</Text>
                <Text style={styles.textStyle1}> FOOD</Text>
                </View>
                <Image
                        style={{height:120,width:120,marginTop:20}}
                        source={require('./Icons/pizza3.png')}
                        />
            </View>
            <View style={styles.buttonContainer}>
                <Pressable style={styles.pressableStyle}  onPress={onConsumerClick}>
                    <Text style={styles.bText}>POTROŠNIK</Text>
                </Pressable>
                <Pressable style={styles.pressableStyle1} onPress={onCompanyClick}>
                    <Text style={styles.bText1}>PONUDNIK</Text>
                </Pressable>
            </View>
            <View style={styles.emptyView}/>
        </MainBackground>
      </View>
    
    );
  };
const styles = StyleSheet.create({
  container: {
      flex:1,
  },
  textContainer: {
    flex:7,
    justifyContent:'flex-end',
    alignItems:'center',
    backgroundColor:'transparent'
  },
  textStyle:{
      color: '#484848',
      fontSize: 40,
      bottom:10,
  },textStyle1:{
    color: '#ffffff',
    fontSize: 40,
    bottom:10,
},
  buttonContainer: {
    flex:6,
    justifyContent:'space-evenly',
    alignItems:'center',
    backgroundColor:'transparent',
    marginTop: 80,
  },
  pressableStyle:{
    borderColor:'rgba(0,0,0,0.8)',
    alignItems:'center',
    justifyContent:'center',
    alignContent:'center',
    width:300,
    height:"30%",
    shadowColor: '#000',
    shadowOffset: { width: 0, height: 10 },
    shadowOpacity: 0.1,
    shadowRadius: 10,
    elevation: 10,
    borderRadius:100,
    backgroundColor:'#ffffff'
  },
  pressableStyle1:{
    borderColor:'rgba(0,0,0,0.8)',
    alignItems:'center',
    justifyContent:'center',
    alignContent:'center',
    width:300,
    height:"30%",
    shadowColor: '#000',
    shadowOffset: { width: 0, height: 10 },
    shadowOpacity: 0.1,
    shadowRadius: 10,
    elevation: 10,
    borderRadius:100,
    backgroundColor:'#484848'
  },
  emptyView:{
      flex:2
  },
  bText: {
    color: '#484848',
    fontSize: 23,
  },bText1: {
    color: '#ffffff',
    fontSize: 23,
  }
});

export default EntranceScreen;
