import React, { useState, useEffect } from 'react'
import { Text, StyleSheet, Image , FlatList, View, Pressable, TextInput, NumberInput, SafeAreaView, ActivityIndicator  } from 'react-native';
import {Picker} from '@react-native-picker/picker';
import { col } from './clr';
import SomeComponent from './CustomBackground';
import { ScrollView } from 'react-native-gesture-handler';


//const data=[{title:"Spagetti", imageUrl:"https://www.edamam.com/web-img/39d/39d10063c69dd8c817be2c7f44175379.jpg",ingredients:["egg", "flour","milk"]},{title:"Spagetti", imageUrl:"https://www.edamam.com/web-img/39d/39d10063c69dd8c817be2c7f44175379.jpg",ingredients:["egg", "flour","milk"]}];

const movieURL = "https://api.edamam.com/api/recipes/v2?type=public&q=flour&app_id=61b96bf9&app_key=a28fc7e347025174ec9cfe2ea3fcfc63&field=label&field=image&field=ingredientLines";



const RecepiesScreen = ({navigation}) => {


  const [isLoading, setLoading] = useState(false);
  const [data, setData] = useState( [
     {
      "img": "https://www.edamam.com/web-img/e11/e11bbf894747fe466fc53d5b83590aec.jpg",
      "ingr":  [
        "4 1/2 tablespoons (1.4 ounces) all-purpose flour",
        "1 cup granulated sugar",
        "1 cup whole milk",
        "1 cup (2 sticks) unsalted butter, cubed and softened at room temperature",
        "1/2 teaspoon vanilla extract",
        "Pinch of salt",
      ],
      "title": "Flour Buttercream Recipe",
    },
     {
      "img": "https://www.edamam.com/web-img/a45/a45af7122f17001665cb694713f1c3ec.jpg",
      "ingr":  [
        "500g plain white flour or wholemeal flour, plus extra",
        "2 tsp bicarbonate of soda",
        "½ tsp fine salt",
        "1 tbsp sugar",
        "350ml-400ml natural yogurt or buttermilk",
        "handful of oats or seeds, optional",
      ],
      "title": "Plain flour bread",
    },
     {
      "img": "https://www.edamam.com/web-img/a05/a05d1efa48496c6bb047eceae88e3ef8.jpg",
      "ingr":  [
        "2 1/4 cups chickpea flour",
        "1/4 cup extra-virgin olive oil, plus 3 tbsp.",
        "1 tbsp. salt",
      ],
      "title": "Chickpea-Flour Crêpe",
    },
     {
      "img": "https://www.edamam.com/web-img/d9c/d9cbd2be28c28c0a9b185bdfe79b8fe2.jpg",
      "ingr":  [
        "1 teaspoon kosher salt",
        "3/4 cup cold water",
        "2 3/4 cups all-purpose flour, plus more for dusting the work surface",
        "1/3 cup shortening",
      ],
      "title": "Homemade Flour Tortillas",
    },
     {
      "img": "https://www.edamam.com/web-img/4ed/4ed3a2abd64b7571934c5fed05680de9.jpg",
      "ingr":  [
        "2 cups cake flour",
        "1 teaspoon baking powder",
        "1 teaspoon salt",
        "3 tablespoons vegetable shortening",
        "3/4 cup warm water",
      ],
      "title": "Genius Flour Tortillas",
    },
     {
      "img": "https://www.edamam.com/web-img/378/3782fea5e09a37725a5ef034163ffade.jpg",
      "ingr":  [
        "2 cups all-purpose flour",
        "3 hard-boiled egg yolks, pressed through a fine-mesh sieve",
        "1 teaspoon kosher salt",
        "3/4 cup (1 1/2 sticks) unsalted butter, room temperature",
        "2/3 cup sugar",
      ],
      "title": "Toasted-Flour Sablés",
    },
     {
      "img": "https://www.edamam.com/web-img/85d/85d782300cd3f2b717345700ff29294c.png",
      "ingr":  [
        "- 3 eggs",
        "- 100g white sugar.",
        "- 35g carob flour",
        "- 30g corn starch",
        "-100ml cream",
        "- 2 tbp honey",
        "- 1 tbp sugar",
      ],
      "title": "Carob Flour Rolls",
    },
     {
      "img": "https://www.edamam.com/web-img/d49/d49a1380440235abbb2c3a34e5e218aa.jpg",
      "ingr":  [
        "1 1/2 cups chestnut flour Gold Medal Flour All-Purpose $1.99 thru 11/25",
        "1/8 teaspoon table salt",
        "1 cup milk",
        "3 large eggs, lightly beaten Eggs Large White 2 for $3.00 thru 11/25",
        "1/2 cup (1 stick) unsalted butter, melted",
        "2 tablespoons confectioners' sugar",
        "Chestnut Espresso Caramel-Swirl Ice Cream, for serving",
      ],
      "title": "Chestnut Flour Crepes",
    },
     {
      "img": "https://www.edamam.com/web-img/528/5280aa7f3765c673cc54a20142dd1020.jpg",
      "ingr":  [
        "2 1/2 cup All Purpose Flour",
        "2 1/2 tsp Baking Powder",
        "1 tsp Kosher Salt",
        "1/2 cup Lard or Vegetable Shortening",
        "2 tbsp Lard or Vegetable Shortening",
        "1 cup Hot Water",
      ],
      "title": "Homemade Flour Tortillas",
    },
     {
      "img": "https://www.edamam.com/web-img/483/483b5d3990c1385520d485d04841ffc5.jpg",
      "ingr":  [
        "4 cup unbleached all purpose flour",
        "1 1/4 tsp salt",
        "6 tbsp vegetable shortening (preferably a non-hydrogenated type, such as Spectrum)",
      ],
      "title": "Flour Tortillas",
    },
  ])

  const getData = () => {
    //const [title, setTitle] = useState([]);
  
    useEffect(() => {
      if(isLoading == true){
      fetch(movieURL)
      .then((response) => response.json())
      .then((json) => {
        //console.log(json)
        const rec = [];
        for (let index = 0; index < 10; index++) {
          rec.push({title: json.hits[index].recipe.label, img: json.hits[index].recipe.image, ingr: json.hits[index].recipe.ingredientLines})
        }
        console.log(rec);
        setData(rec)
      })
      .catch((error) => alert(error))
      .finally(setLoading(false));
      }
    });
  }
  const onClick = (ingredients) =>{
    navigation.jumpTo('Nakupovalni list', { list: ingredients });
  }
  
  
  const renderItem = (item) => {
      var ingredients = '';
      item.ingr.forEach(element => {
          ingredients = ingredients + element + '\n'
      });
      return(
          <View style={styles.itemContainer}>
            <View style={{flex:8,justifyContent:'flex-start',alignItems:'center'}}>
              <Image  style={styles.itemImageStyle} source={{uri: item.img}}/>
            </View>
            <View style={{flex:1,justifyContent:'flex-start',alignItems:'center',marginTop:30}}>
                <Text style={styles.itemTitleStyle}>{item.title.toString().toUpperCase()}</Text>
            </View>
            <View style={{flex:4}}>
              <ScrollView>
                <Text style={styles.itemIngredientsStyle}>{ingredients.toUpperCase()}</Text>
                </ScrollView>
            </View>
            <View style={{flex:1,alignItems:'flex-end',flexDirection:'row',marginBottom:18}}>
                <Pressable style={{flex:1,justifyContent:'flex-start',marginTop:10,marginLeft:8}}>
                    <Text style={{fontSize:13,color:col.secondaryColor}}>CART</Text>
                </Pressable>
                <Pressable style={{flex:1,alignItems:'flex-end',marginRight:8}}>
                    <Text style={{fontSize:13,color:col.mainColor}}>COLLECTION</Text>
                </Pressable>
            </View>
              {/* <View style={styles.itemTitleContainer}>
                  <Text style={styles.itemTitleStyle}>{item.title}</Text>
              </View>
              <View style={styles.itemImageContainer}>
                  <Image  style={styles.itemImageStyle} source={{uri: item.img}}/>
              </View>
              <View  style={styles.ingredientsContainer}>
                  <Text style={styles.itemIngredientsStyle}>{ingredients}</Text>
              </View>
              <View style={{alignItems:'center',justifyContent:'center'}}>
                <Pressable style={{alignItems:'center',justifyContent:'center',borderWidth:1,
          borderRadius:20,marginTop:10,marginBottom:60 }} onPress={() => onClick(item.ingr)}>
                  <Image style={styles.imageStyle} source={require('./Icons/add.png')}/>
                  </Pressable>
              </View> */}
          </View>
      );
  }
    //getData()
    return (
      <SafeAreaView style={styles.container}>
      {isLoading ? ( <SomeComponent style={{flex:1,justifyContent:"center",alignContent:"center"}}>
          <ActivityIndicator size={70} color={col.secondaryColor} />
        </SomeComponent> ) : (<View style={styles.container}>
            <SomeComponent style={{flex:1}}>
                <FlatList horizontal data={data}
            renderItem={({item}) =>renderItem(item)}/>
            </SomeComponent>
        </View>)}
    </SafeAreaView>
      );
};
const styles = StyleSheet.create({
    container: {
      flex: 1,
    },
      itemContainer:{
        height:"95%",
        width:350,
        alignItems: 'center',
        justifyContent: 'center',
        backgroundColor: col.textColorPrimary,
        borderTopLeftRadius: 25,
        borderTopRightRadius: 25,
        borderBottomLeftRadius:25,
        borderBottomRightRadius:25,
        shadowColor: 'gray',
        shadowOffset: {width: 0, height: 0},
        shadowOpacity: 1,
        shadowRadius: 8,
        elevation: 8,
        justifyContent: 'space-between',
        paddingLeft: 16,
        paddingRight: 16,
        marginTop: 15,
        marginBottom: 20,
        marginLeft: 21,
        marginRight: 15,
      },
      itemTitleContainer:{
          flex:1,
          alignItems:'center',
          justifyContent:'center'
      },
      itemImageContainer:{
          flex:1,
        alignItems:'center',
        justifyContent:'center',
      },
      itemImageStyle:{
          width:320,height:320,borderRadius:20,marginTop:18
      },
      ingredientsContainer:{
          flex:1,
          alignItems:'center',
          justifyContent:'center',
      },
      itemIngredientsStyle:{
          color: '#D3D3D3',
          fontSize: 13,
          textAlign:'center',
      },
      itemTitleStyle:{
        fontSize:20,
        color:'white'
      },
      imageStyle:{
        width:40,
        height:40,
      }
  });

export default RecepiesScreen;
