import React from 'react';
import { Text, StyleSheet, Image , FlatList, View, Pressable } from 'react-native';

const data = [
    {username:"Creativno d.o.o",location:"Ljubljana", description:"Ostanki malic",quantity:10,price:1.5},
    {username:"Domišlija d.o.o",location:"Celje",  description:"Pretečen rok",quantity:100,price:0.5},
    {username:"Kuharji d.o.o.",location:"Maribor", description:"Ostanki kuhinje",quantity:42,price:1},
    {username:"Menza Gorenje d.o.",location:"Velenje", description:"Ostanki menze",quantity:56,price:3},
];


const MealFeedAnimScreen = ({navigation}) => {//adad
    
const onAddPress = () =>{
}
const pressableItem = (item) => {
    return(
        <View style={styles.container}>
            <View style={styles.headerContainer}>
                <View style={styles.textContainer}> 
                    <Text style={styles.usernameStyle}>{item.username}</Text>
                    <Text style={styles.locationStyle}>{item.location}</Text>
                </View>
            </View>
            <View style={styles.descriptionContainer}>
                <View style={{flex:1,flexDirection:"row",alignItems:"center"}}>
                    <Text style={styles.descriptionStyle}>{item.description}</Text>
                    <View style={{justifyContent:"flex-end",alignItems:"center"}}>
                    <Text style={styles.priceStyle}>{item.price}€/kg</Text>
                    </View>
                </View>
                <Text style={styles.descriptionSubStyle}>{item.quantity} (kg)</Text>
            </View>
        </View>

    );
}
    return (
        <FlatList
          data={data}
            renderItem={({item}) =>pressableItem(item)}
          />
      );
};
const styles = StyleSheet.create({
    container: {
      flex: 1,
      backgroundColor: 'transparent',
    },
    headerContainer:{
        flex:1,
        flexDirection:'row',
        height:80,
        borderTopWidth:1,
        borderBottomWidth:1
    },
    profileContainer:{
        flex:1,
        alignItems:'flex-start',
        justifyContent:'center'
    },textContainer:{
        flex:3,
        alignItems:'flex-start',
        justifyContent:'center',
        left:5
    },
    postContainer:{
        flex:8,
        alignItems:'center',
        justifyContent:'center'
    },
    profileStyle:{
        height:60,
        width:60,
    },
    usernameStyle:{
        fontSize:20,
        color:"black"
    },
    postStyle:{
        height:400,
        width:"100%"
    },
    descriptionContainer:{
        width:"100%",
        top:10,
        paddingBottom:20,
    },
    descriptionStyle:{
        fontSize:16,
        paddingHorizontal:10,
    },descriptionSubStyle:{
        fontSize:16,
        paddingHorizontal:15,
    },
    toolBarContainer:{
        height: 50,
        width:"100%",
        flexDirection:'row',
        paddingHorizontal:5
    },
    likeContainer:{
        flex:1,
        justifyContent:'center'
    },
    saveContainer:{
        flex:1,
        justifyContent:'center',
        alignItems:"flex-end",
    },
    locationStyle:{
        fontSize:16,
        left:5
    },
    priceStyle:{
        alignSelf:"flex-end",
        fontSize:20
    }
  });

export default MealFeedAnimScreen;
