import React from 'react';
import { Text, StyleSheet, Image , FlatList, View } from 'react-native';

const data = [
    {username:"Jernej Sadl", profilePicture: require('./Icons/jernej_sadl.png'),postPicture:require('./Icons/golaz.png'),description:"this is sauce"},
    {username:"Lan Vrckovnik", profilePicture:require("./Icons/lan_vrckovnik.png"),postPicture:require('./Icons/spageti.png'),description:"this is sauce"},
    {username:"Aljaz Sovic", profilePicture:require('./Icons/aljaz_sovic.png'),postPicture:require('./Icons/dunajski.png'),description:"this is sauce"},
    {username:"Aljaz Vetrih", profilePicture:require('./Icons/aljaz_vetrih.png'),postPicture:require('./Icons/pica.png'),description:"this is sauce"},
];
const pressableItem = (item) => {
    return(
        <View style={styles.container}>
            <View style={styles.headerContainer}>
                <View style={styles.profileContainer}>
                    <Image source={item.profilePicture} style={styles.profileStyle} ></Image>
                </View>
                <View style={styles.textContainer}> 
                    <Text style={styles.usernameStyle}>{item.username}</Text>
                </View>
            </View>
            <View style={styles.postContainer}>
                <Image source={item.postPicture} style={styles.postStyle}></Image>
            </View>
            <View style={styles.descriptionContainer}>
                <Text style={styles.descriptionStyle}>{item.description}</Text>
            </View>
        </View>

    );
}

const SocialMediaScreen = () => {//adad
    return (
        <FlatList
          data={data}
            renderItem={({item}) =>pressableItem(item)}
          />
      );
};
const styles = StyleSheet.create({
    container: {
      flex: 1,
      backgroundColor: 'transparent',
    },
    headerContainer:{
        flex:1,
        flexDirection:'row',
        height:80,
        backgroundColor:"black"
    },
    profileContainer:{
        flex:1,
        alignItems:'flex-start',
        justifyContent:'center'
    },textContainer:{
        flex:3,
        alignItems:'flex-start',
        justifyContent:'center',
        right:30
    },
    postContainer:{
        flex:8,
        alignItems:'center',
        justifyContent:'center'
    },
    profileStyle:{
        height:60,
        width:60,
    },
    usernameStyle:{
        fontSize:16,
    },
    postStyle:{
        height:400,
        width:"100%"
    },
    descriptionContainer:{
        height:100,
        width:"100"
    },
    descriptionStyle:{
        fontSize:16,
    }
  });

export default SocialMediaScreen;
