import { StatusBar } from "expo-status-bar";
import React, { useState } from "react";
import { LinearGradient } from "expo-linear-gradient";
import { easeGradient } from 'react-native-easing-gradient'
import {col} from "./clr.js"
import { accTypeChange } from "./reduxComponents/accTypeAction.js";

import {
  StyleSheet,
  Text,
  View,
  Image,
  TextInput,
  Button,
  TouchableOpacity,
  ActivityIndicator,
  Pressable,
  BackHandler
} from "react-native";
import SomeComponent from "./CustomBackground.js";
import methods, { registration, signIn } from '../Firebase/methods'
import { connect } from "react-redux";
import { accType } from "./Values/accType.js";
import { bindActionCreators } from "redux";



class CostumerRegistrationScreen extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      email:'none',
      password:'',
      name:'none',
      surname:'',
      loadingStatus:false
    };
  }

  // componentDidMount() {
  //   BackHandler.addEventListener("hardwareBackPress", () => {this.props.accTypeChange(accType.NONE)});
  // }

  // componentWillUnmount() {
  //   BackHandler.removeEventListener("hardwareBackPress",() => {this.props.accTypeChange(accType.NONE)} );
  // }
  // backAction = () =>{
  //   this.props.accTypeChange(accType.NONE)
  // }
  // componentDidMount() {
  //   this.backHandler = BackHandler.addEventListener(
  //     "hardwareBackPress",
  //     this.backAction
  //   );
  // }

  // componentWillUnmount() {
  //   this.backHandler.remove();
  // }
  render(){
  const onRegistrationClick = async () => {
    try {
      if (this.state.email !== '' && this.state.password !== '' && this.state.name !== '' && this.state.surname !== '') {
        //console.log(email)
        await registration(this.state.email,this.state.password,this.state.name,this.state.surname)
        //navigation.navigate('Main')
      }
    } catch (error) {
      console.log(error.message)
    }
  }


  const onBackClick = () =>{
    this.props.accTypeChange(accType.NONE)
  }
  const onLoginClick = async () =>{
      this.props.navigation.navigate('ConsumerLogin')
  }

  return (
    <View style={styles.container}>
      { this.state.loadingStatus ? 
        <View style={{flex:1,justifyContent:"center",alignContent:"center"}}>
          <ActivityIndicator size={70} color={col.secondaryColor} />
        </View> :
        <SomeComponent>
        <View style={styles.container}>
            <View style={{flex:0.5,justifyContent:"flex-end",alignItems:"center"}}>
              <Pressable onPress={onBackClick}>
                <Text>Back</Text>
              </Pressable>
            </View>
            <View style={styles.titleContainer}>
                <Text style={styles.titleText}>Registration C</Text>
            </View>
            <View style={styles.formContainer}>
            <View style={styles.inputView}>
                    <TextInput
                    style={styles.TextInput}
                    placeholder="Name"
                    placeholderTextColor="#003f5c"
                    onChangeText={(name) => this.setState({name:name.replace(' ','')})}
                    />
                </View>
                <View style={styles.inputView}>
                    <TextInput
                    style={styles.TextInput}
                    placeholder="surname"
                    placeholderTextColor="#003f5c"
                    onChangeText={(surname) => this.setState({surname:surname.replace(' ','')})}
                    />
                </View>
                <View style={styles.inputView}>
                    <TextInput
                    style={styles.TextInput}
                    placeholder="Email"
                    placeholderTextColor="#003f5c"
                    onChangeText={(email) => this.setState({email:email.replace(' ','')})}
                    />
                </View>
        
                <View style={styles.inputView}>
                    <TextInput
                    style={styles.TextInput}
                    placeholder="Geslo"
                    placeholderTextColor="#003f5c"
                    secureTextEntry={true}
                    onChangeText={(password) => this.setState({password:password})}
                    />
                </View>
        
                <Pressable onPress={onLoginClick}>
                    <Text style={styles.forgot_button}>Already registrated? Login!</Text>
                </Pressable>
        
                <TouchableOpacity style={styles.loginBtn} onPress={onRegistrationClick}>
                    <Text style={styles.loginText}>LOGIN</Text>
                </TouchableOpacity>
            </View>
        </View>
        </SomeComponent>}
      </View>
  );
      }
}
 
const styles = StyleSheet.create({
  container: {
    flex: 1
  },
 formContainer:{
    flex: 4,
    backgroundColor: "transparent",
    alignItems: "center",
    justifyContent: "center",
 },
  inputView: {
    backgroundColor: col.textColorSecondary,
    borderRadius: 30,
    width: 200,
    height: 45,
    marginBottom: 20,
    alignItems: "center",
  },
 
  TextInput: {
    color: col.clrText,
    fontFamily:"Light",
    textAlign:"center",
    height: 50,
    flex: 1,
  },
 
  forgot_button: {
    color: col.clrText,
    fontFamily:"Regular",
    height: 30,
    marginBottom: 30,
  },
 
  loginBtn: {
    width: 300,
    borderRadius: 25,
    height: 50,
    alignItems: "center",
    justifyContent: "center",
    marginTop: 40,
    backgroundColor: col.secondaryColor,
  },
  loginText: {
    color: col.clrText,
    fontFamily:"Bold",
  },
  titleContainer:{
    flex: 1,
    backgroundColor: "transparent",
    alignItems: "center",
    justifyContent: "flex-end",
  },
  titleText:{
    color: col.clrText,
    fontFamily:"Bold",
    fontSize:40,
  },
});

const mapStateToProps = (state) => {
  const { propertyReducer } = state
  return { propertyReducer }
};
const mapDispatchToProps = dispatch => (
  bindActionCreators({
    accTypeChange:accTypeChange
  }, dispatch)
);

export default connect(mapStateToProps, mapDispatchToProps)(CostumerRegistrationScreen);
